// and used to bootstrap nodes with constraints
import { tsKeywords } from "./tsKeywords.js";
import { formatting } from "./format.js";
import { internal } from "./internal.js";
import { jsObjects } from "./jsObjects.js";
import { parsing } from "./parsing.js";
import { tsGenerics } from "./tsGenerics.js";
import { validation } from "./validation.js";
export const ambientRootScope = schemaScope({
    ...tsKeywords,
    ...jsObjects,
    ...validation,
    ...internal,
    ...tsGenerics,
    parse: parsing,
    format: formatting
    // TODO: remove cast
});
$ark.ambient = ambientRootScope.internal;
export const keywordNodes = ambientRootScope.export();
