import { defineRoot, schemaScope } from "../scope.js";
const trim = defineRoot({
    in: "string",
    morphs: (s) => s.trim()
});
const uppercase = defineRoot({
    in: "string",
    morphs: (s) => s.toUpperCase()
});
const lowercase = defineRoot({
    in: "string",
    morphs: (s) => s.toLowerCase()
});
export const formatting = schemaScope({
    trim,
    uppercase,
    lowercase
}).export();
